import "./NewExpenseForm.css";
import { useState } from "react";
import ExpenseItem from "../Expenses/ExpenseItem";

function NewExpenseForm(props) {
  //   const [userInput, setUserInput] = useState({
  //     title: "",
  //     amount: "",
  //     date: "",
  //   });

  //   function titleChangeHandler(event) {
  //     setUserInput((prevState) => {
  //       return { ...prevState, title: event.target.value };
  //     });
  //   }

  //   function amountChangeHandler(event) {
  //     setUserInput((prevState) => {
  //       return { ...prevState, amount: event.target.value };
  //     });
  //   }

  //   function dateChangeHandler(event) {
  //     setUserInput((prevState) => {
  //       return { ...prevState, date: event.target.value };
  //     });
  //   }

  const [enternedTitle, setEnternedTitle] = useState(enternedTitle);
  const [enternedAmount, setEnternedAmount] = useState(enternedAmount);
  const [enternedDate, setEnternedDate] = useState(enternedDate);

  function titleChangeHandler(event) {
    setEnternedTitle(event.target.value);
  }
  function amountChangeHandler(event) {
    setEnternedAmount(event.target.value);
  }
  function dateChangeHandler(event) {
    setEnternedDate(event.target.value);
  }

  function submitHandler(event) {
    event.preventDefault();

    const expensaData = {
      title: enternedTitle,
      amount: enternedAmount,
      date: enternedDate,
    };
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.10"
            step="0.10"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2022-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button
          type="submit"
          onClick={() => {
            return <ExpenseItem></ExpenseItem>;
          }}
        >
          Add Expense
        </button>
      </div>
    </form>
  );
}

export default NewExpenseForm;
